INSERT INTO State(StateName, Description)
VALUES
    (N'Tiểu bang Alabama', N'Tiểu bang Alabama nằm ở vùng đông nam Hoa Kỳ, giáp với Tennessee về phía bắc, Georgia về phía đông, Florida và vịnh Mexico về phía nam, và Mississippi về phía tây. Alabama là bang rộng thứ 30 và đông dân thứ 24 (2010) của Hoa Kỳ. Ở đây có một trong những thủy lộ nội địa dài nhất đất nước'),
    (N'Tiểu bang Alaska', N'Tiểu bang Alaska là một tiểu bang của Hợp chúng quốc Hoa Kỳ, nằm tại đầu tây bắc của lục địa Bắc Mỹ. Alaska giáp với hai tỉnh Yukon và British Columbia của Canada ở phía đông, giáp với Bắc Băng Dương ở phía bắc, và giáp với Thái Bình Dương ở phía tây và phía nam, đối diện với Nga qua eo biển Bering. Alaska là bang có diện tích lớn nhất trong 50 bang'),
    (N'Tiểu bang Arizona', N'Tiểu bang Arizona là một tiểu bang tại tây nam Hoa Kỳ. Đây cũng là một bang Tây Hoa Kỳ và thuộc vùng núi. Nó là bang lớn thứ sáu về diện tích và đông dân thứ 14. Thủ phủ và thành phố lớn nhất là Phoenix. Arizona là một trong bốn bang Four Corners. Nó tiếp giáp với New Mexico, Utah, Nevada, California, và México, và có một điểm chung với Colorado.'),
    (N'Tiểu bang Arkansas', N'Thủ phủ Arkansas là Little Rock. Arkansas là tiểu bang duy nhất của Hoa Kỳ mà có kim cương tự nhiên (gần Murfreesboro). Do đó, đồng quarter Mỹ đặc biệt của Arkansas có một chiếc kim cương trên mặt trái (có chung quanh thân gạo và một con vịt trời bay trên hồ).Biên giới phía đông của Arkansas là sông Mississippi, trừ ở hai quận Clay và Greene, ở đấy sông Saint Francis là biên giới phía tây của "Gót giày ống Missouri". Arkansas bên cạnh Louisiana về phía nam, Missouri về phía bắc, Tennessee và Mississippi về phía đông, và Texas và Oklahoma về phía tây. Arkansas là miền đẹp có nhiều núi và thung lũng, rừng rậm, và đồng bằng tốt. Miền Tây Bắc Arkansas là một phần của Cao nguyên Ozark, bao gồm dãy núi Boston; vào miền nam có dãy núi Ouachita. Những vùng này được chia theo sông Arkansas; những vùng đông và nam của Arkansas được gọi Vùng đất thấp.'),
    (N'Tiểu bang California', N'Tiểu bang California là một tiểu bang ven biển phía tây của Hoa Kỳ. Với dân số là 38,9 triệu người và diện tích 423.970 km², California là tiểu bang đông dân nhất Hoa Kỳ và lớn thứ ba theo diện tích. California tiếp giáp bang Oregon ở phía bắc, Nevada và Arizona ở phía đông, Baja California của Mexico ở phía nam và Thái Bình Dương ở phía tây. Thủ phủ của tiểu bang là thành phố Sacramento.'),
    (N'Tiểu bang Colorado', N'Tiểu bang Colorado là một trong ba tiểu bang có các ranh giới nằm trên vĩ tuyến và kinh tuyến (hai tiểu bang kia là Wyoming và Utah). Tiểu bang bắt đầu từ 37° đến 41° vĩ độ bắc và từ 102° đến 109° kinh độ tây. Đài kỷ niệm Bốn Gốc ở cực tây nam ở 37° vĩ độ bắc và 109° kinh độ tây.'),
    (N'Tiểu bang Connecticut', N'Tiểu bang Connecticut là tiểu bang Hoa Kỳ thuộc về miền đông bắc Hoa Kỳ.Connecticut có khí hậu ôn hòa với đường bờ biển dài quanh vịnh Long Island. Điều này đã giúp bang có truyền thống phát triển ngành hàng hải từ lâu đời. Bang Connecticut hiện nay được biết đến với sự giàu có bậc nhất Hoa Kỳ. Từ thế kỉ 18, tiểu bang đã bắt đầu phát triển công nghiệp chế tạo và các tập đoàn tài chính: những công ty bảo hiểm đầu tiên ở Hartford và quỹ tự bảo hiểm rủi ro đầu tiên ở quận Fairfield. Nhờ đó, Connecticut có thu nhập bình quân đầu người, chỉ số phát triển con người và thu nhập bình quân hộ gia đình cao nhất Hoa Kỳ'),
    (N'Tiểu bang Delaware', N' là tiểu bang thuộc miền Trung Đại Tây Dương của Hoa Kỳ. Nó là một trong 13 tiểu bang đầu tiên của Mỹ và được gọi là "Tiểu bang Thứ nhất" vì nó là tiểu bang đầu tiên phê chuẩn Hiến pháp Hoa Kỳ. Delaware là tiểu bang nhỏ thứ hai của Hoa Kỳ. Nó giáp với Pennsylvania về phía bắc; với sông Delaware và Đại Tây Dương về phía đông; và với Maryland về phía tây và nam. Một số phần nhỏ của Delaware nằm vào bên xa, tức là bên đông, của cửa sông Delaware; và những mảnh đất này có biên giới trên đất với New Jersey. Cùng với các quận Bờ biển Đông của Maryland và hai quận của Virginia, tiểu bang Delaware là một phần của bán đảo Delmarva, một đơn vị địa lý kéo dài xuống bờ biển Trung Đại Tây Dương.'),
    (N'Tiểu bang Florida', N'là một tiểu bang ở đông nam bộ của Hợp chúng quốc Hoa Kỳ, giáp vịnh Mexico ở phía tây, giáp Alabama và Georgia ở phía bắc, giáp Đại Tây Dương ở phía đông, và ở phía nam là eo biển Florida. Florida là tiểu bang rộng lớn thứ 22, đông dân thứ 4, và có mật độ dân số đứng thứ 8 trong số 50 tiểu bang của Hoa Kỳ. Thủ phủ là Tallahassee, thành phố lớn nhất là Jacksonville, và vùng đô thị Miami là vùng đô thị lớn nhất Về mặt địa thế, phần lớn lãnh thổ Florida là một bán đảo nằm giữa vịnh Mexico, Đại Tây Dương, và eo biển Florida. Florida có đường bờ biển dài nhất trong số 48 bang liền kề của Hoa Kỳ, với xấp xỉ 1.350 dặm (2.170 km), và là tiểu bang duy nhất tiếp giáp vịnh Mexico lẫn Đại Tây Dương. Địa hình Florida không có núi non, đất đai trũng thấp không cao hơn mực nước biển là bao, cấu tạo bởi đất trầm tích. Khí hậu Florida gồm vùng cận nhiệt đới ở phía bắc; còn phía nam có khí hậu nhiệt đới'),
    (N'Tiểu bang Georgia', N'Georgia nằm giáp với Florida về phía nam, với Đại Tây Dương và Nam Carolina về phía đông, với Alabama về phía tây, và với Tennessee và Bắc Carolina về phía bắc. Miền bắc của tiểu bang này nằm trên dãy núi Blue Ridge, một dãy núi thuộc hệ thống núi của dãy Appalachian. Vùng piedmont ở miền trung kéo dài từ chân núi tới đường thác nước, ở đường này các sông chảy xuống thác nước tới cao độ của đồng bằng ven biển của lục địa ở phần nam của tiểu bang. Nơi cao nhất ở Georgia là Brasstown Bald (núi Brasstown) có cao độ 1.458 mét (4.784 foot); những nơi thấp nhất của tiểu bang bằng với mực nước biển.Thủ phủ của Georgia là Atlanta'),
    (N'Tiểu bang Hawaii', N'Là tiểu bang Hoa Kỳ nằm hoàn toàn trên quần đảo Hawaiʻi (ngày xưa được gọi quần đảo Sandwich bởi những người Châu Âu), nằm trong Thái Bình Dương cách lục địa khoảng 3.700 kilômét (2.300 dặm). Khi bang được gia nhập Liên bang ngày 21 tháng 8 năm 1959, Hawaiʻi được trở thành tiểu bang thứ 50 của Hoa Kỳ. Tên của tiểu bang trong tiếng Hawaiʻi có dấu ʻokina (ʻ), cho nên tiếng Anh chuẩn của Hawaiʻi (thổ ngữ Hawaiʻi) cũng phải được viết với dấu này. Đây là tiểu bang có tỷ lệ cư dân gốc Á cao nhất tại Hoa Kỳ.'),
    (N'Tiểu bang Idaho', N'Idaho giáp với Washington, Oregon, Nevada, Utah, Montana, Wyoming, và tỉnh British Columbia của Canada, biên giới Idaho–BC kéo dài 77 kilômét (48 dặm). Phong cảnh có đất gồ ghề và vài trong những vùng lớn nhất được để yên trong nước Mỹ. Tiểu bang này thuộc về dãy núi Rocky và có cảnh hay và rất nhiều tài nguyên. Nó có những dãy núi cao ngất có phủ tuyết, thác ghềnh, hồ yên ổn, và hẻm núi sâu. Nước của sông Snake chảy qua hẻm Hells, nó sâu hơn Grand Canyon. Thác Shoshone xuống những vách đá gồ ghề từ cao hơn thác Niagara.'),
    (N'Tiểu bang Illinois', N'Illinois là tiểu bang thứ 21 của Hoa Kỳ, được gia nhập Liên bang vào năm 1818. Illinois là tiểu bang đông dân nhất vùng Trung Tây nước Mỹ và đứng thứ năm toàn liên bang về dân số. Chicago nằm ở phía đông bắc là thành phố lớn nhất và trung tâm kinh tế hàng đầu tại Illinois. Đây cũng là thành phố lớn thứ ba nước Mỹ, chỉ sau thành phố New York và Los Angeles. Thủ phủ của tiểu bang đặt tại thành phố nhỏ Springfield'),
    (N'Tiểu bang Indiana', N'Indiana giáp với hồ Michigan và tiểu bang Michigan về phía bắc, với Ohio về phía đông, với Kentucky về phía nam theo đường chảy của sông Ohio, và với Illinois về phía tây. Indiana thuộc về vùng Ngũ Đại Hồ.'),
    (N'Tiểu bang Iowa', N'là một tiểu bang thuộc miền Trung Tây Hoa Kỳ. Iowa giáp với Minnesota về phía bắc, Nebraska và Nam Dakota về phía tây, Missouri về phía nam, và Wisconsin và Illinois về phía đông. Nó có hai tên hiệu là "Tiểu bang Ngô cao" (The Tall Corn State) và "Tiểu bang Hawkeye" (The Hawkeye State) theo tên Black Hawk'),
    (N'Tiểu bangKansas', N'là tiểu bang ở miền Trung Tây Hoa Kỳ. Tiểu bang này được đặt tên theo sông Kansas chảy qua tiểu bang, và tên của sông bắt nguồn từ tiếng Sioux Kansa có nghĩa là "dân tộc của gió nam". Kansas là trung tâm địa lý của đất liền Hoa Kỳ.'),
    (N'Tiểu bangKentucky', N'Thịnh vượng chung Kentucky (tiếng Anh: Commonwealth of Kentucky, phát âm như "Ken-tắc-ky") là tiểu bang thứ 15 của Hoa Kỳ. Kentucky thường được coi là tiểu bang miền Nam.Đây là nơi sinh của Tổng thống Abraham Lincoln (tại Hardin County).'),
    (N'Tiểu bangLouisiana', N'à một tiểu bang tọa lạc ở miền Nam Hoa Kỳ. Louisiana là bang rộng lớn thứ 31 và bang đông dân thứ 25 trong 50 tiểu bang của Hoa Kỳ. Thủ phủ là Baton Rouge, còn thành phố lớn nhất là New Orleans. Louisiana là bang duy nhất của Hoa Kỳ mà phân cấp hành chính được gọi là "parish", tương đương với "county" ở các bang khác. Parish lớn nhất theo dân số là Đông Baton Rouge, và lớn nhất theo diện tích là Plaquemines. Louisiana tiếp giáp với Arkansas về phía bắc, Mississippi về phía đông, Texas về phía tây, và vịnh México về phía nam'),
    (N'Tiểu bangMaine', N'là tiểu bang cực Đông của vùng New England thuộc Đông Bắc Hoa Kỳ. Nó giáp New Hampshire về phía Tây, Vịnh Maine về phía Đông Nam, và các tỉnh của Canada là New Brunswick và Quebec về phía Đông Bắc và Tây Bắc. Nó là tiểu bang lớn nhất vùng New England nếu tính theo tổng diện tích. Trong 50 tiểu bang của nước Mỹ thì Maine là bang nhỏ thứ 12 theo diện tích, ít dân thứ 9, mật độ dân số thấp thứ 13 và là vùng nông thôn nhất  Maine cũng là tiểu bang cực Bắc phía Đông của Ngũ Đại Hồ, tiểu bang duy nhất có tên bao gồm một âm tiết và tiểu bang có biên giới duy nhất với một tiểu bang khác của Hoa Kỳ. Khoảng một nửa diện tích của Maine nằm ở mỗi bên của vĩ tuyến 45 Bắc theo vĩ độ. Thành phố đông dân nhất ở Maine là Portland, trong khi thủ phủ của nó là Augusta'),
    (N'Tiểu bangMaryland', N'là một tiểu bang vùng Trung Đại Tây Dương của Hoa Kỳ, nằm trên bờ biển phía đông của Hoa Kỳ.'),
    (N'Tiểu bang Massachusetts', N'là tiểu bang đông dân nhất của khu vực New England thuộc vùng Đông Bắc Hoa Kỳ. Phía đông Massachusetts giáp với Đại Tây Dương, phía nam giáp với các tiểu bang Connecticut và Rhode Island, phía bác giáp với New Hampshire và Vermont, và phía tây giáp với tiểu bang New York. Thủ phủ của Massachusetts và thành phố lớn nhất của New England là Boston.'),
    (N'Tiểu bang Michigan', N'là một tiểu bang thuộc vùng Bắc-Đông Bắc của Hoa Kỳ, giáp tỉnh Ontario của Canada. Bang này được đặt tên theo hồ Michigan, vốn có xuất xứ từ tiếng Ojibwe là mishigami có nghĩa là "hồ nước lớn". Michigan là bang đông dân thứ 8 trong 50 bang của Mỹ. Thủ phủ của nó là Lansing, thành phố lớn nhất là Detroit. Michigan chính thức được sáp nhập vào liên bang vào ngày 26 tháng 1 năm 1837 và trở thành bang thứ 26 của Hoa Kỳ'),
    (N'Tiểu bang Minnesota', N' là một tiểu bang vùng Trung Tây của Hoa Kỳ. Minnesota được thành tiểu bang thứ 32 của Hoa Kỳ vào ngày 11 tháng 5 năm 1858. Nó được tạo ra từ lãnh thổ của Minnesota và Wisconsin. Cái tên Minnesota bắt nguồn từ tên mini sota trong tiếng Dakota cho sông Minnesota, nghĩa là "nước trong xanh."'),
    (N'Tiểu bang Mississippi', N'Mississippi là một tiểu bang phía nam của Hoa Kỳ. Tên của tiểu bang lấy từ tên sông Mississippi, chảy dọc theo biên giới phía tây. Cái tên đó có nguồn gốc hoặc là từ tiếng Ojibwe, một loại tiếng của người bản địa Bắc Mỹ được nói ở thượng nguồn dòng sông, hoặc là trong tiếng Algonquian, với nghĩa là "sông lớn". Một số tên hiệu khác được gán cho Mississippi là Magnolia State và Hospitality State.'),
    (N'Tiểu bang Missouri', N'được đặt tên theo tiếng bộ lạc Siouan nghĩa là "thành phố của nhiều tàu lớn", là một tiểu bang ở vùng trung tâm của Hoa Kỳ. Được xem bởi những người cư ngụ ở đó như là một tiểu bang vùng Trung Tây nhưng có rất nhiều ảnh hưởng văn hóa của miền nam. Tên hiệu của tiểu bang là Show-Me State. Sông Mississippi và sông Missouri là hai con sông lớn chảy qua tiểu bang này.'),
    (N'Tiểu bang Montana', N'Montana là một tiểu bang nằm ở miền tây bắc Hoa Kỳ, là bang thứ 41 gia nhập liên bang vào ngày 8 tháng 11 năm 1889. Thủ phủ của Montana là thành phố Helena, còn thành phố lớn nhất là Billings và Great Falls.'),
    (N'Tiểu bang Nebraska', N'là một tiểu bang thuộc vùng Đồng bằng Lớn và Trung Tây của Hoa Kỳ. Tiểu bang Nebraska giáp với tiểu bang Nam Dakota về phía bắc, Iowa về phía đông, Missouri về phía đông nam, Kansas về phía nam, Colorado về phía tây nam và Wyoming về phía tây. Tiểu bang có diện tích hơn 200.000 km² và dân số khoảng 1,9 triệu người. Thủ phủ của bang là thành phố Lincoln và thành phố đông dân nhất là Omaha nằm bên sông Missouri'),
    (N'Tiểu bang Nevada', N'à một tiểu bang nằm ở miền tây Hoa Kỳ, là tiểu bang thứ 36 gia nhập liên bang vào năm 1864. Thủ phủ của Nevada là thành phố Carson còn thành phố lớn nhất là Las Vegas. Nevada có biệt hiệu là Battle Born State ("Bang ra đời trong chiến tranh") để kỷ niệm việc Nevada gia nhập liên bang đứng về phía miền bắc trong cuộc Nội chiến Mỹ giữa thế kỉ 19.'),
    (N'Tiểu bang New Hampshire', N'là một tiểu bang thuộc vùng New England ở phía đông-bắc của Hoa Kỳ, tiếp giáp với tỉnh bang Quebec của Canada. Tiểu bang này bảy vùng khác nhau: Seacoast, Dartmouth - Lake Sunapee, Lakes Region, Merrimack Valley, Monadnock Region, White Mountains và Great North Woods.'),
    (N'Tiểu bang New Jersey', N'New Jersey là một tiểu bang ở vùng Trung Đại Tây Dương và Đông Bắc của Hoa Kỳ. Phía bắc và phía đông giáp với bang New York; phía đông, đông nam và nam giáp Đại Tây Dương; phía Tây giáp với sông Delaware và Pennsylvania; phía tây nam giáp với Vịnh Delaware và bang Delaware. New Jersey là tiểu bang xếp thứ 4 trong danh sách những tiểu bang nhỏ nhất nhưng xếp thứ 11'),
    (N'Tiểu bang New Mexico', N'là một tiểu bang tọa lạc ở vùng Tây Nam Hoa Kỳ, giáp biên giới quốc tế với bang Chihuahua của México. New Mexico trở thành bang thứ 47 vào ngày 6 tháng 1 năm 1912. Nó thường được xem là một tiểu bang miền Núi. New Mexico có diện tích lớn thứ năm, dân số lớn thứ 36, và mật độ dân số thấp thứ sáu trong 50 tiểu bang Hoa Kỳ'),
    (N'Tiểu bang New York', N' là một tiểu bang của Hoa Kỳ. Tiểu bang New York giáp với các bang khác của Hoa Kỳ là Vermont, Massachusetts, Connecticut về phía đông, giáp với hai bang New Jersey, Pennsylvania về phía nam, đồng thời giáp với hai tỉnh của Canada là Quebec và Ontario về phía bắc. Tiểu bang New York là tiểu bang có dân số đông thứ tư tại Hoa Kỳ.'),
    (N'Tiểu bang North Carolina', N'là một tiểu bang ở vùng Đông Nam Hoa Kỳ. Bắc Carolina là bang lớn thứ 28 và đông dân thứ 9 trong số 50 bang của Hoa Kỳ. Tiểu bang này giáp với Virginia về phía bắc, Đại Tây Dương ở phía đông, Georgia và Nam Carolina về phía nam, và Tennessee về phía tây. Raleigh là thủ phủ của bang và Charlotte là thành phố lớn nhất của nó. Vùng đô thị Charlotte,'),
    (N'Tiểu bang North Dakota', N' là một tiểu bang Hoa Kỳ, xa nhất về phía bắc của các tiểu bang trong khu vực Đồng bằng Lớn thuộc Trung Tây Hoa Kỳ, mặc dù trong suốt thế kỉ 19 được xem như là một phần của miền tây hoang dã, giáp hai tỉnh Manitoba và Saskatchewan của Canada. Sông Missouri chảy qua phần phía tây của tiểu bang này, tạo thành hồ Sakakawea sau đập Garrison.'),
    (N'Tiểu bang Ohio', N' là một tiểu bang khu vực Trung Tây (cũ) nằm ở miền đông bắc Hoa Kỳ. Tên "Ohio" theo tiếng Iroquois có nghĩa là "sông đẹp" và đó cũng là tên của một dòng sông dùng làm ranh giới phía nam của tiểu bang này với tiểu bang Kentucky.'),
    (N'Tiểu bang Oklahoma', N'là một tiểu bang nằm ở miền nam Hoa Kỳ. Với dân số 3,6 triệu người vào năm 2007 và diện tích 177.847 km², Oklahoma là tiểu bang có dân số đứng hàng thứ 28 và diện tích đứng hàng thứ 20 liên bang.'),
    (N'Tiểu bang Oregon', N'là một tiểu bang nằm ở miền nam Hoa Kỳ. Với dân số 3,6 triệu người vào năm 2007 và diện tích 177.847 km², Oklahoma là tiểu bang có dân số đứng hàng thứ 28 và diện tích đứng hàng thứ 20 liên bang.'),
    (N'Tiểu bang Pennsylvania', N'Là một trong 13 tiểu bang đầu tiên của nước Mỹ từng là thuộc địa cũ của Đế quốc Anh, Pennsylvania được biết đến với các tên gọi Keystone State, phù hợp với vị trí trung tâm giữa các thuộc địa nguyên thủy, hay Quaker State. Với công nghiệp chế tạo Conestoga wagon, súng trường và thuốc lá, Pennsylvania là một khu vực chuyển tiếp xuyên quốc gia có nhiều đặc điểm của cả công nghiệp hóa triệt để của miền bắc và nông nghiệp của miền nam.'),
    (N'Tiểu bang Rhode Island', N'Đây là tiểu bang có diện tích nhỏ nhất nước Mỹ với dân số ít thứ 7, nhưng lại là bang có mật độ dân số cao thứ hai, chỉ sau New Jersey. Tuy tên gọi dịch sang tiếng Việt là Đảo Rhode, phần lớn tiểu bang lại nằm trong lục địa. Về vị trí địa lý, Rhode Island giáp với Connecticut về phía tây, Massachusetts về phía bắc và phía đông, và Đại Tây Dương ở phía nam. Một phần nhỏ đường biên hàng hải của Rhode Island trùng với New York'),
    (N'Tiểu bang South Carolina', N'là một bang thuộc phía đông nam của Hoa Kỳ. Phía bắc giáp North Carolina, phía đông nam giáp Đại Tây Dương và phía tây nam giáp Georgia dọc theo sông Savannah. South Carolina là một trong 13 thuộc địa nổi dậy chống lại sự thống trị của Anh trong Cách mạng Hoa Kỳ vào ngày 23 tháng 5 năm 1788. Nó là bang đầu tiên ly khai khỏi Liên bang để thành lập Liên minh miền Nam ngày 20 tháng 12 năm 1860. Bang này được đặt tên theo tên vua Charles I của Anh, bởi Carolus là tiếng Latin cho tên Charles.'),
    (N'Tiểu bang South Dakota', N' là một trong năm mươi tiểu bang của nước Mỹ, nằm ở trung bắc Mỹ, phía bắc giáp North Dakota, nam giáp Nebraska, tây giáp Wyoming, đông giáp Missouri. South Dakota còn có một tên gọi khác là "tiểu bang Đỉnh Rushmore". Thủ phủ của South Dakota là thành phố Pierre.'),
    (N'Tiểu bang Tennessee', N'tên chính thức là Tiểu bang Tennessee, là một bang không giáp biển ở khu vực Đông Nam của Hoa Kỳ. Tennessee là bang lớn thứ 36 theo diện tích và đông dân thứ 15 trong số 50 bang. Nó giáp với Kentucky ở phía Bắc, Virginia ở phía Đông Bắc, Bắc Carolina ở phía Đông, Georgia, Alabama và Mississippi ở phía Nam, Arkansas ở phía Tây Nam và Missouri ở phía Tây Bắc. Tennessee được phân chia về mặt địa lý, văn hóa và pháp lý thành ba Đại phân khu với tên gọi Đông, Trung và Tây Tennessee. Thành phố Nashville là thủ phủ và thành phố lớn nhất của bang'),
    (N'Tiểu bang Texas', N'là tiểu bang đông dân thứ hai và có diện tích lớn thứ hai trong số 50 tiểu bang của Hợp chúng quốc Hoa Kỳ, và là tiểu bang lớn nhất trong số 48 tiểu bang liền kề của Hoa Kỳ. Về mặt địa lý, Texas nằm ở vùng Trung Nam của quốc gia, có biên giới quốc tế với các bang Chihuahua, Coahuila, Nuevo León, và Tamaulipas của México; bên trong Hoa Kỳ, Texas có biên giới với tiểu bang New Mexico ở phía tây, Oklahoma ở phía bắc, Arkansas ở phía đông bắc, và Louisiana ở phía đông. Texas có diện tích 696.200 kilômét vuông (268.800 dặm vuông Anh) với 26,1 triệu cư dân'),
    (N'Tiểu bang Utah', N'là một tiểu bang miền tây của Hoa Kỳ. Đây là bang thứ 45 được gia nhập vào hiệp chúng quốc vào ngày 4 tháng 1 năm 1896. Khoảng 88% dân số của tiểu bang Utah sống trong một khu vực đô thị lớn là Wasatch Front với thành phố Salt Lake là trung tâm. Trong khi đó, một phần lớn diện tích tiểu bang không có dân cư sinh sống đã biến Utah trở thành bang có mức độ đô thị hóa cao thứ 6 tại Hoa Kỳ[1]. Cái tên "Utah" được bắt nguồn từ ngôn ngữ của người da đỏ Ute bản địa, có nghĩa là "những con người của vùng núi"'),
    (N'Tiểu bang Vermont', N'là một tiểu bang ở vùng New England của Hoa Kỳ. Tiểu bang này giáp với các bang Massachusetts ở phía nam, New Hampshire về phía đông, và New York về phía tây, và tỉnh Quebec của Canada về phía bắc. Vermont là tiểu bang duy nhất ở New England không giáp với Đại Tây Dương. Vermont là tiểu bang có dân số ít thứ hai của Hoa Kỳ và nhỏ thứ sáu về diện tích trong số 50 tiểu bang của Hoa Kỳ với dân số được ghi nhận là 643.503 người theo điều tra dân số Hoa Kỳ năm 2020 . Thủ phủ của bang là Montpelier,'),
    (N'Tiểu bang Virginia', N'ên chính thức là Thịnh vượng chung Virginia (tiếng Anh: Commonwealth of Virginia), là một bang nằm tại khu vực Nam Đại Tây Dương của Hợp chúng quốc Hoa Kỳ. Virginia có tên hiệu là "Old Dominion" do từng là một lãnh thổ tự trị cũ của quân chủ Anh,'),
    (N'Tiểu bang Washington', N'là một tiểu bang ở vùng Tây Bắc Thái Bình Dương của Hoa Kỳ, phía bắc giáp với tỉnh British Columbia của Canada, phía nam giáp với Oregon. Thủ phủ của tiểu bang là Olympia còn thành phố lớn nhất là Seattle. Tiêu bang lấy tên của George Washington, tổng thống đầu tiên của nước Mỹ. Tổng diện tích: 184.827 km², dân số: 7 triệu người (năm 2013), mật độ dân số: 39,6 người/km².'),
    (N'Tiểu bang West Virginia', N'à một tiểu bang Hoa Kỳ trong vùng Appalachia, nói chung được biết đến như là The Mountain State. West Virginia ly khai khỏi Khối thịnh vượng chung Virginia trong suốt Nội chiến Hoa Kỳ và được chấp nhận vào Liên Bang như là một bang tách biệt ngày 20 tháng 6 năm 1863 (ngày kỉ niệm West Virginia Day ở tiểu bang này). Đây là bang duy nhất được thành lập do kết quả trực tiếp của nội chiến Mỹ.'),
    (N'Tiểu bang Wisconsin', N'là một tiểu bang miền Trung Tây của Hoa Kỳ. Nền kinh tế vùng đồng quê vốn dựa vào lông thú, sau đó là khai thác gỗ, trồng trọt, chăn nuôi bò. Việc công nghiệp hóa bắt đầu vào cuối thế kỉ 19 tại vùng đông nam, với Milwaukee là một trung tâm chính. Trong những thập kỉ gần đây những kỹ nghệ dịch vụ, đặc biệt là giáo dục và y khoa, đã phát triển khá mạnh.'),
    (N'Tiểu bang Wyoming', N'là một tiểu bang miền núi, nằm ở mạn tây Hoa Kỳ. Đây là tiểu bang rộng thứ 10, ít dân nhất, và thưa dân thứ 2 đất nước. Wyoming giáp Montana về phía bắc, South Dakota và Nebraska về phía đông, Colorado về phía nam, Utah về phía tây nam, Idaho và Montana về phía tây. Dân số bang năm 2018 ước tính là 577.737 người, tức ít người hơn 31 thành phố Hoa Kỳ, trong đó có Denver lân cận'),
    (N'Tiểu bang Washington D.C.', N'còn được gọi là Washington hoặc D.C., là thủ đô và là đặc khu liên bang duy nhất của Hoa Kỳ Đây là một trong những thành phố được ghé thăm nhiều nhất ở Hoa Kỳ, với hơn 20 triệu du khách vào năm 2016'),
    (N'Tiểu bang Puerto Rico', N'Puerto Rico nằm ở phía đông bắc vùng biển Caribbean, phía tây giáp nước Cộng hòa Dominica và phía đông là Quần đảo Virgin. Lãnh thổ Puerto Rico bao gồm một quần đảo trong đó bao gồm đảo chính Puerto Rico và nhiều đảo nhỏ hơn xung quanh, lớn nhất là các đảo Vieques, Culebra và Mona.');
INSERT INTO Major
VALUES
    (N'Khoa học máy tính - Công nghệ thông tin',N'Mỹ là quốc gia đang dẫn đầu về lĩnh vực máy tính, công nghệ trên toàn thế giới. Việc du học Mỹ ngành Công nghệ thông tin sẽ giúp bạn có cơ hội tiếp cận với nền giáo dục tân tiến, cơ sở vật chất hiện đại cùng nhiều cơ hội nghề nghiệp hấp dẫn.'),
    (N'Ngành kinh doanh và quản trị',N'Khi du học Mỹ ngành quản trị kinh doanh, bạn sẽ có nhiều lợi thế hơn học ngành này tại đất nước khác. Trước tiên, Mỹ là trung tâm kinh tế vững mạnh toàn cầu cùng chất lượng đào tạo số một thế giới. Đặc biệt tại Mỹ có rất nhiều trung tâm tài chính cũng như trụ sở của các công ty tài chính phát triển, có bề dày lịch sử. Vì vậy khi theo học ngành này tại Mỹ, cơ hội việc làm sau này của bạn vô cùng rộng mở, cơ hội đầu quân cho các công ty lớn trên thế giới là rất lớn. Bên cạnh đó, tấm bằng ngành Quản trị kinh doanh tại Mỹ được công nhận trên toàn thế giới và thu hút các nhà tuyển dụng hàng đầu'),
    (N'Ngành khoa học xã hội và nhân văn',N'Ngành khoa học xã hội và nhân văn là một lĩnh vực nghiên cứu các chủ đề liên quan đến đời sống con người, bao gồm: xã hội, văn hoá, lịch sử, kinh tế, chính trị, tâm lý học, tâm sinh lý học, pháp luật,... KHXH&NV tập trung vào việc phân tích, diễn giải và hiểu rõ những khía cạnh khác nhau của xã hội và con người, giúp cải thiện chất lượng cuộc sống và đóng góp vào sự phát triển của xã hội.'),
    (N'Ngành nghệ thuật sáng tạo - thiết kế',N'Ngành nghệ thuật sáng tạo và thiết kế là ngành học mà sinh viên khi theo đuổi sẽ được làm quen và trau dồi những kiến thức phù hợp, thực tế với công việc sau này trong nhiều các khía cạnh đa dạng khác nhau liên quan tới nghệ thuật và thiết kế như: âm nhạc (music producer, hướng nghiệp nghề ca sĩ, guitarist,...), mỹ thuật (ngành hội hoạ), sân khấu điện ảnh (ngành diễn viên điện ảnh, ngành nhiếp ảnh, cameraman,...), múa, thiết kế thời trang (các bản thiết kế thời trang)'),
    (N'Ngành kiến trúc - xây dựng',N'Cơ hội việc làm ngành kiến trúc ở Mỹ cực kỳ rộng mở trong những năm gần đây. Đây là một trong những ngành thiếu hụt nhân sự tại Mỹ và nhiều nước trên thế giới. Do đó, chính sách việc làm và định cư cho ngành này rất thông thoáng')
INSERT INTO UniversityType
    (N'Các trường đại học công lập',N'Các trường đại học công lập là các tổ chức giáo dục đại học được tài trợ bởi các phương tiện công cộng, nghĩa là chúng được nhà nước tài trợ chứ không phải các trường đại học tư. Những trường đại học này có quy mô khác nhau; tuy nhiên, chúng thường có quy mô lớn hơn so với trường tư và có đặc điểm là học phí thấp hơn.'),
    (N'Đại học tư thục',N'Các trường đại học tư không được chính phủ tiểu bang tài trợ và chiếm một số lượng đáng kể trong số lượng tuyển sinh ở Hoa Kỳ. Tuy nhiên, do thực tế là chúng không được tài trợ bằng tiền của người nộp thuế mà chủ yếu dựa vào các khoản quyên góp, tài trợ và học phí. , các trường đại học này có xu hướng có học phí cao hơn các trường đại học công lập.'),
    (N'Các trường đại học tư thục vì lợi nhuận',N'Các trường đại học tư thục vì lợi nhuận khác biệt với các trường phi lợi nhuận vì mục tiêu của họ không chỉ là giáo dục sinh viên mà còn tạo ra doanh thu cho các cổ đông và họ có xu hướng hướng tới việc kiếm tiền. Tuy nhiên, điều này không nhất thiết có nghĩa là các trường đại học này cũng không cung cấp nền giáo dục chất lượng. Trên thực tế, có rất nhiều trường đại học tư thục vì lợi nhuận với chương trình giảng dạy tuyệt vời.'),
    (N'Các trường đại học tư thục phi lợi nhuận',N'Mục đích chính của các trường đại học tư thục phi lợi nhuận không phải là trở thành tổ chức có lợi nhuận cao nhất mà là cung cấp nền giáo dục chất lượng cao. Các tổ chức này thường có danh tiếng tốt hơn so với các tổ chức vì lợi nhuận do họ tập trung hơn vào việc sản xuất các chương trình giảng dạy và giáo dục chất lượng cao.'),
    (N'Các trường đại học nghiên cứu',N'Các trường đại học nghiên cứu có thể là trường đại học công lập và tư thục phi lợi nhuận, được coi là các cơ sở cấp bằng tiến sĩ chi hàng triệu đô la cho chi phí nghiên cứu và có các hoạt động nghiên cứu cao.'),
    (N'Các trường Ivy League',N'Các trường Ivy League bao gồm tám trường đại học có trụ sở tại Đông Bắc Hoa Kỳ và là một hội nghị thể thao của các trường đại học Hoa Kỳ. Tuy nhiên, mối liên kết giữa các trường đại học này vượt ra ngoài thể thao và tất cả đều được đánh giá cao về thành tích học tập xuất sắc, tinh hoa xã hội và tính chọn lọc trong tuyển sinh.')
INSERT INTO ProgramType
    (N'Du học tự túc',N'hình thức du học trong đó du học sinh tự chi trả các chi phí liên quan đến du học.  Có một số xu hướng du học phổ biến với học sinh, sinh viên Việt Nam.'),
    (N'Du học học bổng toàn phần',N'là gói học bổng bao gồm 100% học phí và các khoản tiền sinh hoạt (tiền ăn, tiền nhà ở, tiền bảo hiểm), và các chi phí học tập khác như tiền tại liệu, vật dụng để học tập và nghiên cứu.'),
    (N'Du học học bổng 1 phần',N'Dạng học bổng này thường do các trường, các tổ chức xã hội, các công ty, các các nhân… cung cấp. Nó có thể là học bổng cả một khóa học, hoặc chỉ một vài năm học, một vài kỳ học, hoặc thậm chí chỉ một vài tháng, hay chỉ là một khóa học ngắn hạn như học bổng trao đổi sinh viên.')
